import {React,  useEffect, useState } from "react";
import { useLocation, useParams,useNavigate, Navigate, createSearchParams } from "react-router-dom";
import axios from 'axios';
import Header from "../layouts/Header";


function AppointmentForm()
{

    const location = useLocation();
    const dentistId = new URLSearchParams(location.search).get('dentistId');
    const [appointment,setAppointment] = useState({
        date: "",
        time: "",
        duration: 0,
        therapy: ""
        
        
      });
    const [patient, setPatient] = useState("");
    const [description, setDescription] = useState(false);
    const [therapy,setTherapy] = useState("");
   
    const navigate = useNavigate();

    function handleChange(event)
    {
        const { name, value } = event.target;

        if(name==="therapy" && value==="Other")
        {
          console.log("i get here!")
          setDescription(true);
          

        }else if(name==="therapy" && value!=="Other"){
          setDescription(false);
        }

        setAppointment(prevValue => {
            return {
              ...prevValue,
              [name]: value
            };
          });
    }

    function handleChangeOther(event)
    {
        const { name, value } = event.target;

        setTherapy(value)
    }

    function updateSSN(event)
    {
      setPatient(event.target.value);
    }


    
    const handleNewAppointment = async(event) =>
    {
        event.preventDefault();
        try{
            if(appointment.therapy==="Other") //pass the therapy if other is selected
            {
              appointment.therapy= therapy;
            }
            const response = await axios.post(`http://localhost:8080/appointments/addAppointment?dentistId=${dentistId}&ssn=${patient}&time=${appointment.time}`, 
              JSON.stringify(appointment),
              {
                headers: {"Content-Type": "application/json"},
                withCredentials:false
              }
            );
            //const queryParams = {submittedName:patient.name, submittedSurname:patient.surname, result: ""};
            if(response.status===201)
            {
             // queryParams.result = "true";
              console.log("Succesful submit");
              
              
            }else{
              //queryParams.result = "fail";
              console.log("Unsuccesful submit");
            }
            
          }catch(err){
            console.log(err);
            
          };
          
          
    }
    

    return (
          <div>
          <Header title= {"ToothHub"}/>
          <div style={{width:"30%", height:"100%", marginTop:"2%"}}>
            
            <h2 style={{textAlign:"left", paddingLeft:"10px", color:"white"}}>New Appointment Form</h2>

            <form onSubmit={handleNewAppointment}>

              <label for="fname" style={{color:"white"}}>Date:</label><br/>
              <input type="date"  name="date"  onChange= {handleChange} value={appointment.date} style={{float:"left", paddingLeft:"15px"}}/>
              <br/>   
              <br/>
              <br/>
              <label for="lname" style={{color:"white"}}>Start Time:</label><br/>
              <input type="time"  name="time" onChange= {handleChange} value= {appointment.time} style={{float:"left", paddingLeft:"15px"}} min="09:00" max="18:00" required/>
              <br/>   
              <br/>
              <br/>
              <label for="ssn" style={{color:"white"}}>Duration:</label><br/>
              <input type="text"  name="duration" onChange= {handleChange} value={appointment.duration} style={{float:"left", paddingLeft:"15px"}}/>
              <br/>   
              <br/>
              <br/>
              <label for="ssn" style={{color:"white"}}>SSN:</label><br/>
              <input type="text"  name="ssn" onChange= {updateSSN}  value={patient} style={{float:"left", paddingLeft:"15px"}}/>
              <br/>   
              <br/>
              <br/>
              <label for="therapy" style={{color:"white"}}>Treatment: </label><br/>
              <select name="therapy" onChange={handleChange} style={{padding:"10px 10px"}}>
                <option value="Tooth Extraction">Tooth Extraction</option>
                <option value="Tooth Filling" selected>Tooth Filling </option>
                <option value="Root Canal">Root Canal</option>
                <option value="Tooth Cleaning">Tooth Cleaning</option>
                <option value="Dental Bridge">Dental Bridge</option>
                <option value="Other">Other</option>
              </select>
              <br/>   
              <br/>
              <br/>
              {(description===true) ? (<input type="text"  name="therapy" onChange= {handleChangeOther}  value={therapy} style={{float:"left", paddingLeft:"15px"}}/>) : null}
              <br/>   
              <br/>
              <br/>
              
              <button style={{display: "inline"}}>Save</button>
                
              

            </form>

        </div>
        </div>
        );











}

export default AppointmentForm;
