import React, { useState, useEffect } from "react";
import { useLocation, useParams,useNavigate, Navigate, createSearchParams } from "react-router-dom";
import axios from "axios";

import Header from "../layouts/Header";
import Card from "../layouts/Card";


function AppointmentDetails(props)
{

    const location = useLocation();
    const id = new URLSearchParams(location.search).get('appointmentId');
    
    const [appointment, setAppointment] = useState({});

    useEffect(() => {
        axios.get(`http://localhost:8080/appointments/getAppointment/${id}`)
          .then(response => {
            setAppointment(response.data);
          })
          .catch(error => {
            console.error('Error fetching appointment data:', error);
          });
      }, [id]);

      const patientName = appointment.patient ? appointment.patient.name : "";
      const patientSurname = appointment.patient ? appointment.patient.surname : "";
      const patientAge = appointment.patient ? appointment.patient.age : "";
      const patientEmail = appointment.patient ? appointment.patient.email : "";

      const details ={type:"appointment", firstRow:appointment.date, secondRow:appointment.time, thirdRow: appointment.duration};
    return <div>
        <Header title= {"ToothHub"}/>

        <main>
        <Card details= {details} />
        </main>

    </div>



}


export default AppointmentDetails;
