import {React,  useEffect, useState } from "react";
import Header from "../layouts/Header";
import { useLocation, useParams,useNavigate, createSearchParams } from "react-router-dom";
import SideBar from "../layouts/SideBar";
import axios from 'axios';

function DentistList(props)
{
    
    const [dentists, setDentists] = useState([]);
    
    const navigate = useNavigate();

    
    const fetchData = async () => {
        try {
          const response = await axios.get(`http://localhost:8080/administrator/getAllDentists/`);
          setDentists(response.data);
        } catch (error) {
          console.error('An error occurred:', error);
        }
      };
  
     


    /*function navigateToDentistForm()
    {
      navigate(`/dentistpage/${dentistId}/patientForm?dentistId=${dentistId}`, {state: {someData: {dentistId}}});
    }


    const deleteDentist = async (ssn) => {
      const selected_ssn = ssn;
      const selected_patient = dentists.find(dentist => dentist.ssn ===selected_ssn);
      const confirmDelete = window.confirm(`Are you sure you want to delete the selected dentist?\n(Name: ${selected_patient.name}\nLast name: ${selected_patient.surname}\nSsn: ${selected_patient.ssn})`);
      
      if (!confirmDelete) {
        // User clicked cancel, do nothing
        return;
      }
    
      try {
        await axios.delete(`http://localhost:8080/dentists/deleteDentist/${ssn}`);
        console.log('Dentist deleted successfully');
        fetchData(); // Fetch the updated list after deletion
      } catch (error) {
        console.error('An error occurred while deleting:', error);
      }
    };

    function viewDentist(dentist)
    {

      const queryParams = {ssn: dentist.ssn};
      navigate({pathname: `/dentistpage/${dentistId}/patientslist/viewProfile`, search: `?${createSearchParams(queryParams)}`});

    }

    function editDentist(dentist)
    {

      const queryParams = {ssn: dentist.ssn};
      navigate({pathname: `/dentistpage/${dentistId}/patientslist/editProfile`, search: `?${createSearchParams(queryParams)}`});

