    useEffect(() => {
      fetchData();
    }, []);



    return <div >
                
                
                
                  
              

                <main style={{textAlign: "center", height:"100%", width:"50%", marginLeft:"22rem", marginTop : "5rem",padding: "2px"}}>
                
                <button type="button" class="btn btn-secondary" style={{float:"right", marginBottom:"5px"}} onClick={navigateToDentistForm}>Add Dentist</button>
                

                <table className="table table-striped">
                      <thead className="thead-light">
                        <tr>
                          
                          <th scope="col">FirstName</th>
                          <th scope="col">LastName</th>
                          <th score="col">View </th>
                          <th scope="col">Edit</th>
                          <th scope="col">Delete</th>
                        </tr>
                      </thead>
                      <tbody>
                        {dentists.map(dentist => (
                          <tr key={dentist.ssn}>
                            <td>{dentist.name}</td>
                            <td>{dentist.surname}</td>
                            <td><button type="button" className="btn btn-outline-secondary" /*onClick={()=>viewDentist(dentist)}*/>View</button></td>
                            <td><button type="button" className="btn btn-outline-secondary" /*onClick={()=>editDentist(dentist)}*/>Edit</button></td>
                            <td><button type="button" className="btn btn-outline-secondary" /*onClick={()=>deleteDentist(dentist.ssn)}*/>Delete</button></td>
              {/* Add more cells for additional columns */}
                          </tr>
                      ))}
                      </tbody>
                </table>
                
                

 
                </main>

            </div>



}

export default DentistList;
