import React from "react";

function ConfirmationDialog({ appointment, onDelete, onCancel }) {
  return (
    <div className="confirmation-dialog" /*style={{backgroundColor:"#C8FFE0",  zIndex: "9999", position:"fixed", top:"10px", left:"0",display:"flex", width: "100%",height: "100%", justifyContent:"center", alignItems:"center"}}*/>
      <div className="confirmation-content" style={{backgroundColor: "white", padding: "20px", borderRadius: "30px", boxShadow:" 0px 0px 10px rgba(0, 0, 0, 0.3)", textAlign:"center"}}>
      <h3>Confirm Deletion</h3>
      <p>Appointment Date: {appointment.date}</p>
      <p>Appointment Time: {appointment.time}</p>
      <button onClick={onDelete}>Delete</button>
      <button onClick={onCancel}>Cancel</button>
      </div>

    </div>
  );
}

export default ConfirmationDialog;
