import React, { useState, useEffect } from "react";
import { useLocation, useParams,useNavigate, Navigate, createSearchParams } from "react-router-dom";
import axios from "axios";
import Header from "../layouts/Header";


function UpdatedAppointment(props)
{

    const {dentistId} = useParams();
    const navigate = useNavigate();
    const location = useLocation();
    const appointmentId = new URLSearchParams(location.search).get('appointmentId');

    const [appointment, setAppointment] = useState({

        date: "",
        time: "", 
        duration: "",
        
    });

    useEffect(() => {
        loadAppointment();
      }, []);


    const loadAppointment = async () => {
        const result = await axios.get(`http://localhost:8080/appointments/getAppointment/${appointmentId}`);
        setAppointment(result.data);
    };

    const handleChange = (event) => {
        setAppointment({ ...appointment, [event.target.name]: event.target.value });
    };

    const handleSubmit  = async (event) => {
        event.preventDefault();
        await axios.put(`http://localhost:8080/appointments/updateAppointment/${appointmentId}`, appointment);
        navigate(`/dentistpage/${dentistId}/appointmentslist/`);
      };

    return <div>
        <Header title= {"ToothHub"}/>

        <form onSubmit={handleSubmit}>
            <label for="date" style={{color:"white"}}>Date:</label><br/>
            <input type="date"  name="date" onChange= {handleChange} value={appointment.date} style={{float:"left", paddingLeft:"15px"}}/>
            <br/>   
            <br/>
            <br/>
            <label for="time" style={{color:"white"}}>Time:</label><br/>
            <input type="time"  name="time" onChange= {handleChange} value={appointment.time} style={{float:"left", paddingLeft:"15px"}}/>
            <br/>   
            <br/>
            <br/>
            <label for="duration" style={{color:"white"}}>Duration:</label><br/>
            <input type="text"  name="duration" onChange= {handleChange} value={appointment.duration} style={{float:"left", paddingLeft:"15px"}}/>
            <br/>   
            <br/>
            <br/>

            <button type="submit" className="btn btn-outline-primary">
              Submit
            </button>
            <button className="btn btn-outline-danger mx-2" to="/">
              Cancel
            </button>
          </form>
    </div>
    


}

export default UpdatedAppointment;
